import React, { useState } from 'react';
import './App.css';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [registered, setRegistered] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [selectedMovie, setSelectedMovie] = useState('');
  const [registeredUsers, setRegisteredUsers] = useState([]);
  const [submittedMovie, setSubmittedMovie] = useState('');
  const [submitSuccess, setSubmitSuccess] = useState(false);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [ticketQuantity, setTicketQuantity] = useState(1);
  const [paymentMethod, setPaymentMethod] = useState('Credit Card'); // State untuk metode pembayaran
  const [totalPrice, setTotalPrice] = useState(0); // State untuk total harga pembayaran

  // Fungsi untuk login
  const handleLogin = () => {
    // Lakukan logika login di sini
  };

  // Fungsi untuk registrasi
  const handleRegister = () => {
    // Lakukan logika registrasi di sini
  };

  // Fungsi untuk pemilihan film
  const handleMovieSelection = (event) => {
    // Lakukan logika pemilihan film di sini
  };

  // Fungsi untuk saat formulir disubmit
  const handleSubmit = (event) => {
    // Lakukan logika penanganan submit di sini
  };

  // Fungsi untuk menghapus baris
  const deleteRow = () => {
    // Lakukan logika penghapusan di sini
  };

  // Fungsi untuk mengubah jumlah tiket
  const handleTicketQuantityChange = (event) => {
    // Lakukan logika perubahan jumlah tiket di sini
  };

  // Fungsi untuk menghitung total harga berdasarkan jumlah tiket dan harga per tiket
  const calculateTotalPrice = () => {
    // Misalnya harga tiket per film adalah $10
    const ticketPrice = 10;
    const totalPrice = ticketPrice * ticketQuantity;
    setTotalPrice(totalPrice);
  };

  // Event handler untuk memilih metode pembayaran
  const handlePaymentMethodChange = (event) => {
    setPaymentMethod(event.target.value);
  };

  // Event handler untuk menangani pembayaran
  const handlePayment = () => {
    // Lakukan logika pembayaran di sini
  };

  // Render form registrasi atau login tergantung pada status registrasi dan login
  return (
    <div>
      {!loggedIn && !registered ? (
        <div>
          {/* Form registrasi */}
        </div>
      ) : !loggedIn ? (
        <div>
          {/* Form login */}
        </div>
      ) : (
        <div>
          <h1>Movie Ticket Booking</h1>
          {submitSuccess ? (
            <div>
              <h2>Seat Selection</h2>
              {/* Logika untuk memilih kursi */}
              <h2>Payment</h2>
              <label htmlFor="paymentMethod">Select payment method:</label>
              <select id="paymentMethod" onChange={handlePaymentMethodChange} value={paymentMethod}>
                <option value="Credit Card">Credit Card</option>
                <option value="Debit Card">Debit Card</option>
                <option value="PayPal">PayPal</option>
              </select>
              <p>Total Price: ${totalPrice}</p>
              <button onClick={handlePayment}>Pay Now</button>
            </div>
          ) : (
            <div>
              {/* Form untuk memilih film */}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
