import React, { useState } from 'react';

function App() {
  const [selectedMovie, setSelectedMovie] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistered, setIsRegistered] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleMovieSelection = (event) => {
    setSelectedMovie(event.target.value);
  };

  const handleLogin = () => {
    // Anda dapat menambahkan logika autentikasi di sini (misalnya, validasi dengan backend)
    if (username && password) {
      console.log('Login dengan:', username, password);
      setIsLoggedIn(true);
    } else {
      console.log('Harap masukkan username dan password.');
    }
  };

  const handleRegister = () => {
    // Logika registrasi pengguna
    console.log('Register dengan:', username, password);
    setIsRegistered(true);
  };

  return (
    <div>
      <h1>Movie Ticket Booking App</h1>
      {!isLoggedIn && (
        <div>
          {!isRegistered ? (
            <div>
              <h2>Register</h2>
              <label htmlFor="username">Username:</label>
              <input type="text" id="username" value={username} onChange={(e) => setUsername(e.target.value)} />
              <label htmlFor="password">Password:</label>
              <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} />
              <button onClick={handleRegister}>Register</button>
              <p>Sudah memiliki akun? <button onClick={() => setIsRegistered(true)}>Login</button></p>
            </div>
          ) : (
            <div>
              <h2>Login</h2>
              <label htmlFor="username">Username:</label>
              <input type="text" id="username" value={username} onChange={(e) => setUsername(e.target.value)} />
              <label htmlFor="password">Password:</label>
              <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} />
              <button onClick={handleLogin}>Login</button>
              <p>Belum memiliki akun? <button onClick={() => setIsRegistered(false)}>Register</button></p>
            </div>
          )}
        </div>
      )}
      {isLoggedIn && (
        <div>
          <h2>Pilih Film:</h2>
          <select id="movieSelection" onChange={handleMovieSelection} value={selectedMovie}>
            <option value="">Pilih Film</option>
            <option value="film1">Film 1</option>
            <option value="film2">Film 2</option>
            <option value="film3">Film 3</option>
            {/* Tambahkan opsi film lain di sini */}
          </select>
          <div>
            {/* Tampilkan film yang dipilih (opsional) */}
            {selectedMovie && <p>Film yang dipilih: {selectedMovie}</p>}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
