import React, { useState } from 'react';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [selectedMovie, setSelectedMovie] = useState('');
  const [registeredUsers, setRegisteredUsers] = useState([]);

  // Fungsi untuk login
  const handleLogin = () => {
    // Periksa apakah username dan password valid (misalnya, cocok dengan salah satu dari pengguna yang terdaftar)
    const user = registeredUsers.find(user => user.username === username && user.password === password);
    if (user) {
      setLoggedIn(true);
    } else {
      alert('Invalid username or password');
    }
  };

  // Fungsi untuk register
  const handleRegister = () => {
    // Pastikan username belum terdaftar sebelumnya
    const userExists = registeredUsers.some(user => user.username === username);
    if (userExists) {
      alert('Username already exists');
    } else {
      // Tambahkan pengguna baru ke daftar pengguna terdaftar
      setRegisteredUsers(prevUsers => [...prevUsers, { username, password }]);
      alert('Registration successful');
    }
  };

  // Event handler untuk pemilihan film
  const handleMovieSelection = (event) => {
    const selectedMovie = event.target.value;
    setSelectedMovie(selectedMovie);
    console.log('Selected movie:', selectedMovie);
  };

  // Render form login atau pemilihan film tergantung pada status login
  return (
    <div>
      {!loggedIn ? (
        <div>
          <h1>Login</h1>
          <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button onClick={handleLogin}>Login</button>
          <p>Don't have an account? <button onClick={handleRegister}>Register</button></p>
        </div>
      ) : (
        <div>
          <h1>Movie Ticket Booking</h1>
          <label htmlFor="movieSelection">Select a movie:</label>
          <select id="movieSelection" onChange={handleMovieSelection} value={selectedMovie}>
            <option value="">-- Please select a movie --</option>
            <option value="Avengers: Endgame">Avengers: Endgame</option>
            <option value="Spider-Man: Far From Home">Spider-Man: Far From Home</option>
            <option value="The Lion King">The Lion King</option>
            {/* Tambahkan opsi film lainnya sesuai kebutuhan */}
          </select>
          <div>
            {selectedMovie && (
              <p>You selected: {selectedMovie}</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
