import React, { useState } from 'react';

function Form() {
  function handleSubmit(e) {
    e.preventDefault();
    console.log('You clicked submit.');
  }

  return (
    <form onSubmit={handleSubmit}>
      <button type="submit">Submit</button>
    </form>
  );
}

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [registered, setRegistered] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [selectedMovie, setSelectedMovie] = useState('');
  const [registeredUsers, setRegisteredUsers] = useState([]);

  // Fungsi untuk login
  const handleLogin = () => {
    // Periksa apakah username dan password valid
    const user = registeredUsers.find(user => user.username === username && user.password === password);
    if (user) {
      setLoggedIn(true);
    } else {
      alert('Invalid username or password');
    }
  };

  // Fungsi untuk register
  const handleRegister = () => {
    // Tambahkan pengguna baru ke daftar pengguna terdaftar
    setRegisteredUsers(prevUsers => [...prevUsers, { username, password }]);
    setRegistered(true);
    alert('Registration successful');
  };

  // Event handler untuk pemilihan film
  const handleMovieSelection = (event) => {
    const selectedMovie = event.target.value;
    setSelectedMovie(selectedMovie);
    console.log('Selected movie:', selectedMovie);
  };

  // Fungsi untuk mengaktifkan laser
  const activateLasers = () => {
    console.log('Lasers activated!');
    // Tambahkan logika atau fungsi untuk mengaktifkan lasers di sini
  };

  // Event handler untuk saat formulir disubmit
  const handleSubmit = (event) => {
    console.log('You clicked submit.');
    event.preventDefault(); // Untuk mencegah perilaku bawaan saat formulir disubmit
  };

  // Fungsi untuk menghapus baris
  const deleteRow = (id, e) => {
    console.log('Row with id:', id, 'deleted');
    // Tambahkan logika atau fungsi untuk menghapus baris di sini
  };

  // Fungsi untuk menghapus pilihan film
  const clearSelection = () => {
    setSelectedMovie('');
  };

  // Render form registrasi atau login tergantung pada status registrasi dan login
  return (
    <div>
      {!loggedIn && !registered ? (
        <div>
          <h1>Register</h1>
          <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button onClick={handleRegister}>Register</button>
          <p>Already have an account? <button onClick={() => setRegistered(true)}>Login</button></p>
        </div>
      ) : !loggedIn ? (
        <div>
          <h1>Login</h1>
          <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button onClick={handleLogin}>Login</button>
        </div>
      ) : (
        <div>
          <h1>Movie Ticket Booking</h1>
          <form onSubmit={handleSubmit}>
            <label htmlFor="movieSelection">Select a movie:</label>
            <select id="movieSelection" onChange={handleMovieSelection} value={selectedMovie}>
              <option value="">-- Please select a movie --</option>
              <option value="Avengers: Endgame">Avengers: Endgame</option>
              <option value="Spider-Man: Far From Home">Spider-Man: Far From Home</option>
              <option value="The Lion King">The Lion King</option>
              {/* Tambahkan opsi film lainnya sesuai kebutuhan */}
            </select>
            <div>
              {selectedMovie && (
                <div>
                  <img src={`/${selectedMovie.split(' ').join('_')}.jpg`} alt={`${selectedMovie} Poster`} />
                  <p>You selected: {selectedMovie}</p>
                </div>
              )}
            </div>
            <button type="submit">Submit</button>
            {/* Tambahkan tombol Delete Row di sini */}
            <button onClick={(e) => deleteRow(selectedMovie, e)}>Delete Row</button>
          </form>
          <button onClick={activateLasers}>Activate Lasers</button>
          {/* Tambahkan tombol Clear Selection di sini */}
          <button onClick={clearSelection}>Clear Selection</button>
        </div>
      )}
      {/* Hapus komponen Form */}
    </div>
  );
}

export default App;
